      public void actionPerformed(ActionEvent action) {
        
        String word = "";
        File file = new File("C:/Users/User/Downloads/proje/word.txt");
        try{
            Scanner scan1 = new Scanner(file);
            if(scan1.hasNextLine())
            {
                word = scan1.nextLine();
            }
                
        }
        catch(FileNotFoundException e){
            e.printStackTrace();
            
        }
         if( action.getSource() == sports ) 
         {
             try{    
           FileWriter fw=new FileWriter("C:/Users/User/Downloads/proje/givingArticle.txt");    
           SportArticles spr = new SportArticles();
           
           fw.write(spr.findedArticle(word , spr.getArrayList()));    
           
           fw.close();
          }catch(Exception e){System.out.println(e);}    
          System.out.println("Success...");  
         }
        
         if( action.getSource() == business ) 
         {
             try{    
           FileWriter fw=new FileWriter("C:/Users/User/Downloads/proje/givingArticle.txt");    
           BusinessArticles rlg = new BusinessArticles();
           
           fw.write(rlg.findedArticle(word , rlg.getArrayList()));    
           
           fw.close();
          }catch(Exception e){System.out.println(e);}    
          System.out.println("Success...");  
         }
         
         else if( action.getSource() == fashion ) 
         {
             try{    
           FileWriter fw=new FileWriter("C:/Users/User/Downloads/proje/givingArticle.txt");    
           FashionArticles rlg = new FashionArticles();
           
           fw.write(rlg.findedArticle(word , rlg.getArrayList()));    
           
           fw.close();
          }catch(Exception e){System.out.println(e);}    
          System.out.println("Success...");  
         }
         
         else if( action.getSource() == science ) 
         {
             try{    
           FileWriter fw=new FileWriter("C:/Users/User/Downloads/proje/givingArticle.txt");    
           ScienceArticles rlg = new ScienceArticles();
           
           fw.write(rlg.findedArticle(word , rlg.getArrayList()));    
           
           fw.close();
          }catch(Exception e){System.out.println(e);}    
          System.out.println("Success...");  
         }
      }
